dispatch：后代给祖先传值
//定义一个dispatch方法，指定要派发事件名称和数据
function dispatch(eventName, data) {
  let parent = this.$parent;
  // 只要还存在父元素就继续往上查找
  while (parent) {
    // 父元素用$emit触发
    parent.$emit(eventName, data);
    // 递归查找父元素
    parent = parent.$parent;
  }
}

